import json
import sys

card = json.loads(sys.argv[1])
ob_value = sys.argv[2]
action_value = ob_value
ob_type = sys.argv[3]
action_type = ob_type
ob_source = sys.argv[4]
first_seen = sys.argv[5]
intel = sys.argv[6]

card['body'][0]['items'][2]['text'] = first_seen

card['body'][0]['items'][4]['facts'][0]['value'] = ob_source

card['body'][0]['items'][6]['items'].append({
    "type": "TextBlock",
    "text": ob_value,
    "wrap": True,
    "weight": "Bolder"
})
card['body'][0]['items'][6]['items'].append({
                            "type": "FactSet",
                            "facts": [
                                {
                                    "title": "Observable Type",
                                    "value": ob_type.capitalize()
                                },
                                {
                                    "title": "Threat Response Verdict",
                                    "value": intel
                                }
                            ]
                        })
card['body'][0]['items'][6]['items'].append({
    "type": "Input.ChoiceSet",
    "id": action_value + '_' + action_type,
    "choices": [
        {
            "title": "Block",
            "value": "block"
        },
        {
            "title": "Allow",
            "value": "allow"
        },
        {
            "title": "Add Exclusion",
            "value": "exclude"
        }
    ],
    "placeholder": "Select Action for: " + ob_value
})
data = json.dumps(card)
